class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        rows, cols = len(grid), len(grid[0])
        twos = []
        result = 0
        for r in range((rows+1)//2):
            for c in range((cols+1)//2):
                curr = []
                curr.append(grid[r][c])
                if c != cols-c-1:
                    curr.append(grid[r][cols-c-1])
                if r != rows-r-1:
                    curr.append(grid[rows-r-1][c])
                if r != rows-r-1 and c != cols-c-1:
                    curr.append(grid[rows-r-1][cols-c-1])
                if len(curr) == 2:
                    twos.append(curr)
                elif len(curr) == 4:
                    if len(set(curr)) != 1:
                        result += min(Counter(curr).values())
                elif len(curr) == 1:
                    result += 1 if curr[0] == 1 else 0
        diff = 0
        ones = 0
        for a, b in twos:
            if a != b:
                diff += 1
            else:
                if a == 1:
                    ones += 2
        if diff == 0 and ones % 4 == 2:
            result += 2
        else:
            result += diff
        return result
