class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        //cout<<"---"<<endl;
        int ans = 0;
        int n = grid.size();
        int m = grid[0].size();
        for(int i=0;i<grid.size()/2;i++) {
            for(int j=0;j<grid[0].size()/2;j++) {
                int count = grid[i][j]+grid[i][m-j-1]+grid[n-i-1][j]+grid[n-i-1][m-j-1];
                ans+=min(4-count,count);
            }
        }
        //cout<<ans<<endl;

        bool flip = false;
        int ones = 0;
        if(m%2){
            //cout<<"in m"<<endl;
            for(int i=0;i<grid.size()/2;i++) {
                if(grid[i][m/2]!=grid[n-i-1][m/2]) {
                    ans++;
                    flip=true;
                    grid[i][m/2] = 1;
                    grid[n-i-1][m/2] = 1;
                }
                if(grid[i][m/2]) ones++;
                if(grid[n-i-1][m/2]) ones++;
            }
        }
        if(n%2) {
            //cout<<"in n"<<endl;
            for(int j=0;j<grid[0].size()/2;j++) {
                if(grid[n/2][j]!=grid[n/2][m-j-1]) {
                    ans++;
                    flip=true;
                    grid[n/2][j]=1;
                    grid[n/2][m-j-1]=1;
                }
                if(grid[n/2][j]) ones++;
                if(grid[n/2][m-j-1]) ones++;
            }
        }
        if(flip==false) {
            ans+=ones%4;
        }
        //cout<<ans<<endl;
        if((n%2 && m%2) &&grid[n/2][m/2]==1) {
            ans++;
        }
        return ans;



    }
};
