class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ret = 0;
        for (int i = 0; i < n >> 1; i++) {
            for (int j = 0; j < m >> 1; j++) {
                int cnt = grid[i][j] + grid[i][m - j - 1] + grid[n - i - 1][j] + grid[n - i - 1][m - j - 1];
                ret += min(cnt, 4 - cnt);
            }
        }
        int c1 = 0, c2 = 0;
        if (n % 2) {
            for (int j = 0; j < m >> 1; j++) {
                int x = grid[n >> 1][j], y = grid[n >> 1][m - j - 1];
                if (x + y == 1) c1++;
                else if (x + y == 2) c2 += 2;
            }
        }
        if (m % 2) {
            for (int i = 0; i < n >> 1; i++) {
                int x = grid[i][m >> 1], y = grid[n - i - 1][m >> 1];
                if (x + y == 1) c1++;
                else if (x + y == 2) c2 += 2;
            }
        }
        if (c2 % 4 == 0) ret += c1;
        else if (c1) ret += c1;
        else ret += 2;
        if (n % 2 && m % 2) ret += grid[n >> 1][m >> 1];
        return ret;
    }
};
