class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        // Math solution
        // O(N*M)
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;
        int total_1_1_pair = 0;
        int total_1_0_pair = 0;
        if((n&1) && (m&1)){
            ans += grid[n/2][m/2];
        }
        for(int i = 0; i <= ((n-1)/2); i++){
            for(int j = 0; j <= ((m-1)/2); j++){
                if(((n&1) && (m&1)) && (i == (n/2)) && (j == (m/2))) continue;
                // cout << "i = " << i << ", j = " << j << ", before ans = " << ans;
                // coord 1
                int x1 = i;
                int y1 = j;
                // coord 2
                int x2 = n - i - 1;
                int y2 = m - j - 1;
                // coord 3
                int x3 = i;
                int y3 = m - j - 1;
                // coord 4
                int x4 = n - i - 1;
                int y4 = j;
                if(i == (n - i - 1)){
                    if(grid[i][j] == grid[i][m - j - 1]) total_1_1_pair += grid[i][j];
                    else total_1_0_pair++;
                }
                else if(j == (m - j - 1)){
                    if(grid[i][j] == grid[n - i - 1][j]) total_1_1_pair += grid[i][j];    
                    else total_1_0_pair++;
                }
                else{
                    int sum = grid[x1][y1] + grid[x2][y2] + grid[x3][y3] + grid[x4][y4];
                    ans += min(sum, 4 - sum);
                }
                // cout << ", ans after = " << ans << endl;
            }
        }
        // calculation done
        if(total_1_1_pair & 1){
            if(total_1_0_pair){
                ans += total_1_0_pair;
            }
            else{
                ans += 2;
            }
        }
        else{
            ans += total_1_0_pair;
        }
        return ans;
    }
};
