import java.util.Arrays;

class Solution {
    public int minFlips(int[][] grid) {
        int n = grid.length, m = grid[0].length;
        boolean[][] vis = new boolean[n][m];
        int ans = 0;
        // 4个点
        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int x0 = i, y0 = j;
                int x1 = i, y1 = m - 1 - j;
                int x2 = n - 1 - i, y2 = j;
                int x3 = x2, y3 = y1;
                vis[x0][y0] = vis[x1][y1] = vis[x2][y2] = vis[x3][y3] = true;
                int[] cnt = new int[2];
                ++cnt[grid[x0][y0]];
                ++cnt[grid[x1][y1]];
                ++cnt[grid[x2][y2]];
                ++cnt[grid[x3][y3]];
                ans += Math.min(cnt[0], cnt[1]);
                // System.out.println(x0 + " " + y0 + " " + Arrays.toString(cnt));
            }
        }
        int[] cnt = new int[3];
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                if (vis[i][j]) {
                    continue;
                }
                int x0 = i, y0 = j;
                int x1 = i, y1 = m - 1 - j;
                int x2 = n - 1 - i, y2 = j;
                int x3 = x2, y3 = y1;
                vis[x0][y0] = vis[x1][y1] = vis[x2][y2] = vis[x3][y3] = true;
                if (x0 == x2 && y0 == y1) {
                    if (grid[x0][y0] == 1) {
                        ++ans;
                    }
                } else if (x0 == x2) {
                    System.out.println("行对称" + x0 + " " + y0);
                    int x = grid[x0][y0], y = grid[x1][y1];
                    if (x == y) {
                        ++cnt[x];
                    } else {
                        ++cnt[2];
                    }
                } else if (y0 == y1) {
                    System.out.println("列对称" + x0 + " " + y0);
                    int x = grid[x0][y0], y = grid[x2][y2];
                    if (x == y) {
                        ++cnt[x];
                    } else {
                        ++cnt[2];
                    }
                }
            }
        }
        System.out.println(Arrays.toString(cnt));
        int res = Integer.MAX_VALUE;
        // i对1, cnt[1] - i对改成0
        for (int i = 0; i <= cnt[1]; ++i) {
            for (int j = 0; j <= cnt[2]; ++j) {
                int x = 2 * i + 2 * j;
                if (x % 4 == 0) {
                    res = Math.min(res, cnt[2] + (cnt[1] - i) * 2);
                }
            }
        }
        return ans + res;
    }
}
