class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        count2_0, count2_1, count2_2, count1, res = 0, 0, 0, 0, 0
        m, n = len(grid), len(grid[0])

        for i in range((m+1)//2):

            for j in range((n+1)//2):
                if i < m - 1 - i and j < n - 1 - j:
                    total = 4
                    cnt = grid[i][j] + grid[m-1-i][j] + grid[i][n-1-j] + grid[m-1-i][n-1-j]
                if i == m - 1 - i and j < n - 1 - j:
                    total = 2
                    cnt = grid[i][j] + grid[i][n-1-j]
                if i < m - 1 - i and j == n - 1 - j:
                    total = 2
                    cnt = grid[i][j] + grid[m-1-i][j]
                if i == m - 1 - i and j == n - 1 - j:
                    total = 1
                    cnt = grid[i][j]
                res += min(cnt, total-cnt)
                if total == 2:
                    if cnt == 0:
                        count2_0 += 1
                    if cnt == 1:
                        count2_1 += 1
                    if cnt == 2:
                        count2_2 += 1
                if total == 1 and cnt == 1:
                    count1 += 1

        if count1 == 1:
            res += 1
        if count2_2 % 2 == 1:
            if count2_1 == 0:
                res += 2 
        return res
