#define ll long long int
class Solution {
public:
    int minFlips(vector<vector<int>>& f) {
        int n = f.size(), m = f[0].size();
        ll ans = 0;
        ll sec = 0;
        int x = 0, y = n-1;
        while(x<y){
            int a = 0, b = m-1;
            while(a<b){
                int cnt = f[x][a] + f[x][b] + f[y][a] + f[y][b];
                ans += min(cnt,4-cnt);
                a++,b--;
            }
            x++,y--;
        }
        int a = 0, b = m-1;
        int eq_one = 0,zer = 0, un_eq = 0;
        if(n%2){
            if(m%2){
                if(f[n/2][m/2] == 1) ans++;
            }
            while(a<b){
                if(f[n/2][a] == f[n/2][b]){
                    if(f[n/2][a] == 1) eq_one++;
                    else zer++;
                }
                else{
                    un_eq++;
                }
                a++,b--;
            }
        }
        a = 0, b = n-1;
        if(m%2){
            while(a<b){
                if(f[a][m/2] == f[b][m/2]){
                    if(f[a][m/2] == 1) eq_one++;
                    else zer++;
                }
                else{
                    un_eq++;
                }
                a++,b--;
            }
        }
        if(eq_one % 2 == 0){
            ans += un_eq;
        }
        else{
            if(un_eq){
                ans += un_eq;
            }
            else{
                ans += 2;
            }
        }
        return ans;
    }
};
