class Solution {
public:


    int minFlips(vector<vector<int>>& grid) {

        int n = grid.size();
        int m = grid[0].size();

        vector<pair<int,int>>vp;

        for(int i=0;i<=(n-1)/2;i++){

            for(int j=0;j<=(m-1)/2;j++){


                int cnt = 0;

                set<pair<int,int>>st;
                st.insert({i,j});
                st.insert({n-1-i,j});
                st.insert({n-1-i,m-j-1});
                st.insert({i,m-j-1});

                for(auto k:st){
                    cnt += grid[k.first][k.second];
                }

                vp.push_back({st.size(),cnt});

            }
        }

//         for(auto i:vp){
//             cout<<i.first<<" "<<i.second<<endl;
//         }cout<<endl;

        int k = vp.size();
        vector<vector<int>>dp(k+1,vector<int>(4,1e8));

        for(int i=0;i<k;i++){

            int sz = vp[i].first%4;
            int cnt1 = vp[i].second;
            int cnt0 = vp[i].first-vp[i].second;

            if(i==0){
                dp[i+1][0] = cnt1;
                dp[i+1][sz] = min(dp[i+1][sz],cnt0); 
            }else{

                for(int j=0;j<4;j++){
                    dp[i+1][j] = min(dp[i+1][j],dp[i][j]+cnt1);
                    dp[i+1][(j+sz)%4] = min(dp[i+1][(j+sz)%4],dp[i][j]+cnt0);
                }

            }

        }

        return dp[k][0];





    }
};
