class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int four =0;
        int n = grid.size();
        int m = grid[0].size();
        for(int i =0;i<n/2;i++){
            for(int j =0;j<m/2;j++){
                int one = (grid[i][j]+grid[i][m-j-1]+grid[n-i-1][j]+grid[n-i-1][m-j-1]);
                one%=4;
                if(one == 3 ) one=1;
                four+=one;
            }
        }
        int x =0,y=0;
        if(n%2!=0){
            int i = n/2;
            for(int j =0;j<m/2;j++){
                int a = grid[i][j],b = grid[i][m-j-1];
                if(a+b==0) continue;
                else if(a+b==1) x++;
                else if(a+b==2) y++;
                else cout<<"ERROR"<<endl;
            }
        }
        if(m%2!=0){
            int j = m/2;
            for(int i = 0;i<n/2;i++){
                int a = grid[i][j],b=grid[n-i-1][j];
                if(a+b==0) continue;
                else if(a+b==1) x++;
                else if(a+b==2) y++;
                else cout<<"ERROR"<<endl;
            }
        }
        int two =0;
        if((x+y)%2!=0){
            if(x>0) two=x;
            else{
                two=(x+2);
            }
        }
        else{
            two =x;
        }
        int one = 0;
        if(n%2!=0 && m%2!=0){
            if(grid[n/2][m/2]==1) one =1;
        }
        cout<<four<<" "<<two<<" "<<one<<endl;
        return four + two +one ;
    }

};
