class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n, m = len(grid), len(grid[0])

        res = 0
        tc = [0, 0, 0]
        for i in range((n+1)//2):
            for j in range((m+1)//2):
                s = 0
                pts = set([(i, j), (i, m-j-1), (n-i-1, j), (n-i-1, m-j-1)])
                for x, y in pts:
                    s += grid[x][y]
                if len(pts) == 1:
                    res += s
                elif len(pts) == 2:
                    tc[s] += 1
                else:
                    if s == 1 or s == 2:
                        res += s
                    elif s == 3:
                        res += 1

        tc[2] = tc[2] % 2
        if tc[2] and tc[1] >= 1:
            res += 1
            tc[1] -= 1
            tc[2] -= 1
        res += tc[1] + tc[2] * 2

        return res
