class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int vis[n][m];
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                vis[i][j]=0;
            }
        }
        int ans=0;
        int oo=0,zz=0,oz=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(vis[i][j])continue;
                int x1=i,y1=j;
                int x2=n-1-i,y2=j;
                int x3=i,y3=m-1-j;
                int x4=n-1-i,y4=m-1-j;
                set<pair<int,int>>s;
                s.insert({x1,y1});
                s.insert({x2,y2});
                s.insert({x3,y3});
                s.insert({x4,y4});
                if(s.size()==1){
                    if(grid[i][j]==1){
                        ans++;
                        vis[i][j]=1;
                    }
                }
                if(s.size()==2){
                    int o1=0,z1=0;
                    for(pair<int,int> p :s){
                        if(grid[p.first][p.second]==1)o1++;
                        else z1++;
                        vis[p.first][p.second]=1;
                    }
                    if(o1==1 and z1==1)oz++;
                    if(o1==2)oo++;
                    if(z1==2)zz++;
                }
                if(s.size()==4){
                    int o1=0,z1=0;
                    for(pair<int,int> p :s){
                        if(grid[p.first][p.second]==1)o1++;
                        else z1++;
                        vis[p.first][p.second]=1;
                    }
                    ans+=(4-max(o1,z1));
                }
            }
        }
        int ones=oo*2;
        ans+=oz;
        while(oz--){
            if(ones%4==0){
                return ans;
            }
            ones+=2;
        }
        if(ones%4==0)return ans;
        ans+=2;
        return ans;
    }
};
