class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(),m=grid[0].size();
        vector<vector<int>>vis(n,vector<int>(m,0));
        int ans =0;
        int flag=0,one=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(!vis[i][j]){
                    set<pair<int,int>>st;
                    st.insert({i,j});
                    st.insert({i,m-1-j});
                    st.insert({n-1-i,j});
                    st.insert({n-1-i,m-1-j});
                    int cnt=0;
                    for(auto ch : st){
                        vis[ch.first][ch.second] = 1;
                      if(grid[ch.first][ch.second]) cnt++;
                    }
                    if(st.size()==1) ans += cnt;
                    else if(st.size()==2){
                      if(cnt==1) flag=1;
                      one += cnt;
                      ans += min(cnt,2-cnt);
                    }
                   else{ int x = st.size()-cnt,y=cnt;
                    ans += min(x,y);
                   }
                }
            }
        }
        if(one%4!=0 && flag==0) ans +=2;
        return ans;
    }
};
