class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n, m, ans, k, z = len(grid), len(grid[0]), 0, 0, 0
        for i in range(n//2):
            for j in range(m//2):
                a,b,c,d = grid[i][j],grid[n-1-i][j],grid[i][m-1-j],grid[n-1-i][m-1-j]
                u = (a,b,c,d).count(1)
                ans += 4-max(u, 4-u)
        if m%2:
            for i in range(n//2):
                if grid[i][m//2] and grid[n-1-i][m//2]: k += 2
                elif grid[i][m//2] != grid[n-1-i][m//2]: ans, z = ans+1, z+1
        if n%2:
            for j in range(m//2):
                if grid[n//2][j] and grid[n//2][m-1-j]: k += 2
                elif grid[n//2][j] != grid[n//2][m-1-j]: ans, z = ans+1, z+1
        if m%2 and n%2 and grid[n//2][m//2]: k += 1
        if k%4 == 1: return ans+1
        if k%4 == 2:
            if z: return ans
            return ans+2
        if k%4 == 3:
            if z: return ans+1
            return ans+3
        return ans
