class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();
        int ans=0,oneCount=0,avail=0;
        for(int i=0;i<(m+1)/2;i++){
            for(int j=0;j<(n+1)/2;j++){
                int oc=0;
                if(grid[i][j]==1)oc++;
                if(grid[m-1-i][j]==1)oc++;
                if(grid[i][n-1-j]==1)oc++;
                if(grid[m-1-i][n-1-j]==1)oc++;
                if((i==m-1-i) && (j==n-1-j)){
                    if(grid[i][j])ans++;
                }
                else if((i==m-1-i) || (j==n-1-j)){
                    if(oc==2){
                        ans++;
                        avail=1;
                    }
                    else if(oc==4){
                        oneCount+=2;
                    }
                }
                else {
                    ans+=min(oc,4-oc);
                }
            }
        }
        if(oneCount%4){
            if(!avail)ans+=2;
        }
        return ans;
    }
};
