class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int i, j, res1=0, m=grid.size(), n=grid[0].size();
        vector<int> f(2, 0);
        for (i=0;i<m/2;i++) {
            f[0] = 0, f[1]=0;
            for (j=0;j<n/2;j++) {
                f[grid[i][j]]++;
                f[grid[i][n-1-j]]++;
                f[grid[m-1-i][j]]++;
                f[grid[m-1-i][n-1-j]]++;
                res1 += min(f[0], f[1]);
                f[0] = 0, f[1]=0;
                // cout << res1 << " ";
            }

        }
        // cout  << "\n";
        int n1=0, ch=0;
        if (m&1) {
            for (j=0;j<n/2;j++) {
                if (grid[m/2][j]!=grid[m/2][n-1-j]) ch++;
                if (grid[m/2][j]) n1++;
                if (grid[m/2][n-1-j]) n1++;
            }
        }
        if (n&1) {
            for (i=0;i<m/2;i++) {
                if (grid[i][n/2]!=grid[m-1-i][n/2]) ch++;
                if (grid[i][n/2]) n1++;
                if (grid[m-1-i][n/2]) n1++;
            }
        }
        if ((m&1) && (n&1)) {
            // if (grid[m/2][n/2]) n1++;
            if (grid[m/2][n/2]) {
                res1++;
            }
        }
        // cout << n1 << " " << ch << "\n";
        // n1 = n1%4;
        // int n2 = (4-n1)%4;
        // int res2=0;
        // if (ch>=n2) {
        //     res2 = max(ch, min(n2, n1));
        // }
        // else {
        //     res2 = max(ch, n1);
        // }
        if (n1%4==2 && ch==0) res1+=2; 
        res1 += ch;
        return res1;
    }
};
