class Solution {
    public int minFlips(int[][] grid) {
        int count=0;
        int r1=0,r2=grid.length-1;
        int ones=0,options=0;
        while(r1<r2){
            int c1=0,c2=grid[0].length-1;
            while(c1<c2){
                int zero=0,one=0;
                if( grid[r1][c1]==0){zero++;}
                else{
                  one++;  
                } 
                if( grid[r1][c2]==0){zero++;}
                else{
                  one++;  
                } 
                if( grid[r2][c1]==0){zero++;}
                else{
                  one++;  
                } 
                if( grid[r2][c2]==0){zero++;}
                else{
                  one++;  
                } 
                count+=Math.min(zero,one);

                c1++;
                c2--;
            }
            if(c1==c2){
                if(grid[r1][c1]==grid[r2][c1]){
                    if(grid[r1][c1]==1)ones+=2;
                }
                else{
                    count++;
                    options++;
                }
            }
            r1++;
            r2--;
        }
        if(r1==r2){
            int c1=0,c2=grid[0].length-1;   
            while(c1<c2){
                if(grid[r1][c1]==grid[r2][c2]){
                    if(grid[r1][c1]==1)ones+=2;
                }
                else{
                    count++;
                    options++;
                }
                c1++;
                c2--;
            }
            if(c1==c2){
                count+=grid[r1][c1];
            }
        }
        if(ones%4==0 || options>0)return count;
        return count+(ones%4);

    }
}
