class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size(),n=grid[0].size(),ans=0;
        for(int i=0;i<m/2;i++)
        {
            for(int j=0;j<n/2;j++)
            { int cnt=0;
               if(grid[i][j]==0) cnt++;
               if(grid[m-1-i][j]==0) cnt++;
               if(grid[i][n-1-j]==0) cnt++;
               if(grid[m-1-i][n-1-j]==0) cnt++;
               ans+=min(cnt,4-cnt);
            }
        }
        cout<<ans<<endl;
        int one=0,zero=0,cnt=0;
        if(m%2==1)
        { for(int j=0;j<n/2;j++)
           { if(grid[m/2][j]!=grid[m/2][n-1-j]) cnt++;
              else if(grid[m/2][j]==0) zero++;
               else one++;
           }
        }
        cout<<ans<<endl;
        if(n%2==1)
        {
            for(int i=0;i<m/2;i++)
            {
                 if(grid[m-1-i][n/2]!=grid[i][n/2]) cnt++;
                 else if(grid[i][n/2]==0) zero++;
                 else one++;
            }
        }
       // cout<<one<<" "<<zero<<" "<<cnt<<endl;
        if(m%2 && n%2 && grid[m/2][n/2]==1) ans++;
        if(one%2)
        { if(cnt>0) ans+=cnt;
          else ans+=2;
          //cout<<ans<<"g"<<endl;
        }
        else
        { 
          ans+=(cnt);
        }
        return ans;
    }
};
