class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:

        n = len(grid)
        m = len(grid[0])
        ans = 0

        if n % 2 == 1 and m % 2 == 1 and grid[n // 2][m // 2] == 1:
            ans += 1

        check = False
        count = 0

        if n % 2 == 1:
            for i in range(m // 2):
                if grid[n // 2][i] != grid[n // 2][m - i - 1]:
                    check = True
                elif grid[n // 2][i] == 1:
                    count += 1

        if m % 2 == 1:
            for i in range(n // 2):
                if grid[i][m // 2] != grid[n - 1 - i][m // 2]:
                    check = True
                elif grid[i][m // 2] == 1:
                    count += 1

        if count % 2 == 0 or check:
            ans += 0
        else:
            ans += 2

        vis = [[0] * m for _ in range(n)]
        for i in range(n):
            for j in range(m):
                if not vis[i][j]:
                    ones = 0
                    zeros = 0

                    if grid[i][j] == 1:
                        ones += 1
                    else:
                        zeros += 1

                    if (m - j - 1) != j and grid[i][m - j - 1] == 1:
                        ones += 1
                    elif (m - j - 1) != j and grid[i][m - j - 1] == 0:
                        zeros += 1

                    if (n - i - 1) != i and grid[n - i - 1][j] == 1:
                        ones += 1
                    elif (n - i - 1) != i and grid[n - 1 - i][j] == 0:
                        zeros += 1

                    if (m - j - 1) != j and (n - 1 - i) != i and grid[n - 1 - i][m - j - 1] == 1:
                        ones += 1
                    elif (m - j - 1) != j and (n - 1 - i) != i and grid[n - 1 - i][m - j - 1] == 0:
                        zeros += 1

                    ans += min(ones, zeros)

                    vis[i][j] = 1
                    vis[n - i - 1][j] = 1
                    vis[i][m - 1 - j] = 1
                    vis[n - 1 - i][m - j - 1] = 1

        return ans
