class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int m = g.size(), n = g[0].size();
        int ans = 0;

        for(int i = 0; i < m/2; i++) {
            for(int j = 0; j < n/2; j++) {
                int cnt = g[i][j] + g[m-1-i][j] + g[i][n-1-j] + g[m-1-i][n-1-j];
                ans += min(cnt, 4-cnt); 
            }
        }
    //    cout << ans << endl;
        int cnt = 0;
        int count = 0;
        if(m & 1) {
            int mid = m / 2;
            for(int i = 0; i < n/2; i++) {
       //         cout << mid << ' ' << i << ' ' << mid << ' ' << n-1-i << endl;
                if(g[mid][i] != g[mid][n-1-i]) cnt++, ans++;
                else if(g[mid][i] == 1) count++;
            }
        }
        cout << cnt << endl;
        if(n & 1) {
            int mid = n/2;
            for(int i = 0; i < m/2; i++) {
         //       cout << i << ' ' << mid << ' ' << m-1-i << ' ' << mid << endl;
                if(g[i][mid] != g[m-1-i][mid]) cnt++, ans++;
                else if(g[i][mid] == 1) count++;
            }
        }
        if(count & 1) {
            if(cnt == 0) ans+=2;
        }
        if((m&1) && (n & 1) && g[(m)/2][(n)/2] == 1) {
            ans += 1;
        }
        return ans;

    }
};
