class Solution {
    public int minFlips(int[][] grid) {
        int m = grid.length, n = grid[0].length;
        int flips = 0;
        for (int i = 0; i < m / 2; ++i) {
            for (int j = 0; j < n / 2; ++j) {
                int a = grid[i][j] + grid[i][n-1-j] + grid[m-1-i][j] + grid[m-1-i][n-1-j];
                flips += Math.min(a, 4 - a);
            }
        }
        if (m % 2 == 1 && n % 2 == 1 && grid[m/2][n/2] == 1) ++flips;
        int one = 0, f = 0;
        if (m % 2 == 1) {
            int[] r = grid[m/2];
            int i = 0, j = n - 1;
            while (i < j) {
                if (r[i] != r[j]) ++f;
                else if (r[i] == 1 && r[j] == 1) one += 2;
                ++i; --j;
            }
        }
        if (n % 2 == 1) {
            int i = 0, j = m - 1, c = n / 2;
            while (i < j) {
                if (grid[i][c] != grid[j][c]) ++f;
                else if (grid[i][c] == 1 && grid[j][c] == 1) one += 2;
                ++i; --j;
            }
        }
        flips += f;
        if (one % 4 == 2 && f == 0) flips += 2;
        return flips;
    }
}
