class Solution {
public:
    int minFlips(vector<vector<int>>& mat) {
        int r = mat.size(), c = mat[0].size(), ans[3] = {};
        ans[1] = r * c + 1;
        for(int x = 0; x < r; ++x)
            for(int y = 0; y < c; ++y) {
                int tx = r - 1 - x, ty = c - 1 - y;
                if(x > tx || y > ty)
                    continue;
                int c = mat[x][y] + mat[x][ty] + mat[tx][y] + mat[tx][ty];
                if(x == tx && y == ty) {
                    ans[2] += c == 4;
                } else if(x != tx && y != ty) {
                    ans[2] += min(c, 4 - c);
                } else {
                    c >>= 1;
                    tie(ans[0], ans[1]) = make_tuple(min(ans[0] + c, ans[1] + 2 - c), min(ans[1] + c, ans[0] + 2 - c));
                }
            }
        return ans[0] + ans[2];
    }
};
