class Solution {
     public  int minFlips(int[][] grid) {
        int m=grid.length/2;
        int n=grid[0].length/2;
        int count=0;
        //2
        //
        int one=0;
        if(2*m+1==grid.length){
            for(int i=0;i<n;i++){
                if(grid[m][i]!=grid[m][grid[0].length-1-i]){
                    count++;
                }else if(grid[m][i]==1){
                    one+=2;
                }
            }
        }
        if(2*n+1==grid[0].length){
            for(int i=0;i<m;i++){
                if(grid[i][n]!=grid[grid.length-1-i][n]){
                    count++;
                }else if(grid[i][n]==1){
                    one+=2;
                }
            }
        }
        int ret=0;
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                int zero=0;
                if(grid[i][j]==1){
                    zero++;
                }
                if(grid[i][grid[0].length-1-j]==1){
                    zero++;
                }
                if(grid[grid.length-1-i][j]==1){
                    zero++;
                }
                if(grid[grid.length-1-i][grid[0].length-1-j]==1){
                    zero++;
                }
                ret+=Math.min(zero,4-zero);
            }
        }
        if(2*m+1==grid.length&&2*n+1==grid[0].length&&grid[m][n]==1){
            ret++;
        }
        ret+=count;
        if(one%4==2&&count==0){
            ret+=2;
        }
        return ret;
    }
}
