func minFlips(grid [][]int) int {
	dp := make([][][]int, len(grid)/2+1)
	for i := 0; i < len(dp); i++ {
		dp[i] = make([][]int, len(grid[0])/2+1)
		for j := 0; j < len(dp[i]); j++ {
			dp[i][j] = []int{-2, -2, -2, -2}
		}
	}
	midI, midJ := len(grid)/2, len(grid[0])/2
	if len(grid)%2 == 0 {
		midI--
	}
	if len(grid[0])%2 == 0 {
		midJ--
	}
	return leetcode0803Q3testGo(grid, 0, 0, 0, 0, midI, midJ, dp)
}
func leetcode0803Q3testGo(grid [][]int, nowI, nowJ, nowChange, nowYu, midI, midJ int, dp [][][]int) int {
	if nowI > midI {
		if nowYu == 0 {
			return nowChange
		}
		return -1
	}
	if r := dp[nowI][nowJ][nowYu]; r != -2 {
		if r == -1 {
			return r
		}
		return nowChange + r
	}
	nextI, nextJ := nowI, nowJ+1
	if nextJ > midJ {
		nextI, nextJ = nowI+1, 0
	}
	Map := make(map[string]int)
	var oneCount, zeroCount int
	indexes := [][]int{{nowI, nowJ},
		{nowI, len(grid[0]) - nowJ - 1},
		{len(grid) - nowI - 1, nowJ},
		{len(grid) - nowI - 1, len(grid[0]) - nowJ - 1}}
	for i := 0; i < len(indexes); i++ {
		x, y := indexes[i][0], indexes[i][1]
		s := strconv.Itoa(x) + "_" + strconv.Itoa(y)
		if _, ok := Map[s]; !ok {
			Map[s] = 1
			r := grid[x][y]
			if r == 0 {
				zeroCount++
			} else {
				oneCount++
			}
		}
	}
	r := leetcode0803Q3testGo(grid, nextI, nextJ, nowChange+oneCount, nowYu, midI, midJ, dp)
	s := (oneCount + zeroCount) % 4
	nextYu := nowYu + s
	if nextYu >= 4 {
		nextYu %= 4
	}
	r1 := leetcode0803Q3testGo(grid, nextI, nextJ, nowChange+zeroCount, nextYu, midI, midJ, dp)
	if r == -1 || (r1 != -1 && r1 < r) {
		r = r1
	}
	if r == -1 {
		dp[nowI][nowJ][nowYu] = -1
	} else {
		dp[nowI][nowJ][nowYu] = r - nowChange
	}
	return r
}
