class Solution {
    public int minFlips(int[][] grid) {
        int m = grid.length, n = grid[0].length;

        int res = 0;
        for (int row = 0; row < m / 2; row++) {
            for (int col = 0; col < n / 2; col++) {
                int now = grid[row][col];
                // 右边
                int right = grid[row][n - col - 1];
                // 下边
                int bottom = grid[m - row - 1][col];
                // 右下位置
                int rightBottm = grid[m - row - 1][n - col - 1];
                int countOne = now + right + bottom + rightBottm;
                res += countOne > 2 ? 4 - countOne : countOne;
            }
        }

        int count = 0;
        int countOne = 0;
        // 横线特殊轴
        if (m % 2 == 1) {
            int row = m / 2;
            for (int col = 0; col < n / 2; col++) {
                if (grid[row][col] != grid[row][n - col - 1]) {
                    count += 1;
                } else if (grid[row][col] == 1) {
                    countOne += 2;
                }
            }
        }

        // 竖线特殊轴
        if (n % 2 == 1) {
            int col = n / 2;
            for (int row = 0; row < m / 2; row++) {
                if (grid[row][col] != grid[m - row - 1][col]) {
                    count += 1;
                } else if (grid[row][col] == 1) {
                    countOne += 2;
                }
            }
        }

        if (countOne % 4 != 0 && count == 0) {
            res += 2;
        }

        // 中值
        if (m % 2 == 1 && n % 2 == 1 && grid[m / 2][n / 2] == 1) {
            count += 1;
        }

        return res + count;
    }
}
