class Solution {
public:
    int N;
    vector<vector<int>> a;
    set<vector<pair<int,int>>>s;
    vector<vector<pair<int,int>>>S;
    int solve(int i, int rem, vector<vector<int>> &dp) {
        if(i == N) {
            rem %= 4;
            if(rem == 0) {
             return 0;
            }
            return 1e8;
        }

        if(dp[i][rem] != -1) return dp[i][rem];

        int ans = 1e9;

        int o = 0;
        int z = 0;
        int sz = (int)S[i].size();
        for(int j = 0 ; j < sz ; j++) {
            int x = S[i][j].first;
            int y = S[i][j].second;
            if(a[x][y] == 1) {
                o++;
            } else {
                z++;
            }
        }
        // convert all to zeros
        ans = min(ans,solve(i+1,rem,dp) + (sz-z));
        ans = min(ans,solve(i+1,(rem+sz)%4,dp) + (sz-o));
        return dp[i][rem] = ans;
    }
    int minFlips(vector<vector<int>>& _a) {
        a = _a;
        int n = (int)a.size();
        int m = (int)a[0].size();
        for(int i = 0 ; i < (n+1) / 2 ; i++) {
            for(int j = 0 ; j < (m+1) / 2 ; j++) {
                set<pair<int,int>>ids;
                ids.insert({i,j});
                ids.insert({n-1-i,j});
                ids.insert({i,m-1-j});
                ids.insert({n-1-i,m-1-j});
                vector<pair<int,int>>b;
                for(auto &u : ids) {
                    b.push_back({u.first,u.second});
                }
                sort(b.begin(),b.end());
                s.insert(b);
            }
        }
        N = (int)s.size();
        int cnt = 0;
        for(auto &x : s) {
            vector<pair<int,int>>b;
            for(auto &u : x) {
                b.push_back({u.first,u.second});
            }
            // cout << (int)b.size() << ' ';
            // for(auto &u : b) {
            //     cout << u.first << ' ' << u.second << ' ';
            // }
            // cout << '\n';
            S.push_back(b);
        }
        N = (int)S.size();
        vector<vector<int>>dp(N+2,vector<int>(6,-1));
        return solve(0,0,dp);
    }
};
