class Solution {
public:
    int minFlips(vector<vector<int>>& arr) {
        int n = arr.size(), m = arr[0].size();

        int ans = 0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int ct1 = arr[i][j];
                ct1 += arr[i][m-(j+1)];
                ct1 += arr[n-(i+1)][j];
                ct1 += arr[n-(i+1)][m-(j+1)];

                int ct0 = 4-ct1;
                ans += min(ct1, ct0);
            }
        }

        int total_one = 0;
        int total_change = 0;

        if(m&1){
            int ct1 = 0, change = 0;
            for(int up=0, down=n-1;up<down;up++,down--){
                ct1 += arr[up][m/2];
                ct1 += arr[down][m/2];
                if(arr[up][m/2] != arr[down][m/2]) change++;
            }

            ans += change;
            total_one += ct1;
            total_change += change;

            // if(change == 0 && (ct1%4 != 0)){
            //     ans += 2;
            // }
        }

        if(n&1){
            int ct1 = 0, change = 0;
            for(int l=0, r=m-1;l<r;l++,r--){
                ct1 += arr[n/2][l];
                ct1 += arr[n/2][r];
                if(arr[n/2][l] != arr[n/2][r]) change++;
            }

            ans += change;
            total_one += ct1;
            total_change += change;

            // if(change == 0 && (ct1%4 != 0)){
            //     ans += 2;
            // }
        }

        if(total_change == 0 && (total_one%4 != 0)){
                ans += 2;
            }

        if((n&1) && (m&1)){
            if(arr[n/2][m/2] == 1)ans++;
        }

        return ans;


    }
};
