class Solution {
public:
    int minFlips(vector<vector<int>> &A) {
        int n = A.size() , m = A[0].size();
        int cnt = 0 , twos = 0 , zo = 0;
        for(int i = 0 ; i < n / 2 + (n & 1) ; i++){
            for(int j = 0 ; j < m / 2 + (m & 1) ; j++){
                int ones = A[i][j] + (i != n - i - 1 ? A[n - i - 1][j] : 0) + (j != m - j - 1 ? A[i][m - j - 1] : 0) + (i != n - i - 1 && j != m - j - 1 ?  A[n - i - 1][m - j - 1] : 0);
                int cells = 1 + (i != n - i - 1) + (j != m - j - 1) + (i != n - i - 1 && j != m - j - 1);
                if(cells == 3 || cells == 4) cnt += min(ones , 4 - ones);
                else if(cells == 2){
                    if(ones == 2) twos++;
                    if(ones == 1) cnt++ , zo = 1;
                }
                else if(cells == 1){
                    cnt += ones;
                }
            }
        }
        if(twos % 2 == 1){
            if(zo == 0) cnt += 2;
        }
        return cnt;

    }
};
