class Solution {
public:
    int minFlips(vector<vector<int>>& g){
        int n=g.size();
        int m=g[0].size();
        int ans=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int cnt0=0;
                if(g[i][j]==0){
                    cnt0++;
                }
                if(g[i][m-j-1]==0){
                    cnt0++;
                }
                if(g[n-i-1][j]==0){
                    cnt0++;
                }
                if(g[n-i-1][m-j-1]==0){
                    cnt0++;
                }


                if(cnt0>2){
                    ans+=4-cnt0;
                }
                else{
                    ans+=cnt0;
                }
            }
        }
       // cout<<ans<<endl;
        map<pair<int,int>,int> mp;
        if(m%2==1 && n%2==1){
            int p10=0;
            int p11=0;
            for(int j=0;j<m/2;j++){
                 mp[{g[n/2][j],g[n/2][m-j-1]}]++;
            }
            p10=mp[{1,0}]+mp[{0,1}];
            p11=mp[{1,1}];
            mp.clear();



            for(int i=0;i<n/2;i++){
                 mp[{g[i][m/2],g[n-i-1][m/2]}]++;
            }
            p10+=mp[{1,0}]+mp[{0,1}];
            p11+=mp[{1,1}];


            if(p11%2==0){
                ans+=p10;
            }
            else{
                if(p10>0){
                    ans+=p10;
                }
                else{
                    ans+=p10;
                    if(p11>0)
                    ans+=2;
                }
            }

            if(g[n/2][m/2]==1){
                ans++;
            }

        }
       else if(n%2==1){
            int p10=0;
            int p11=0;
            for(int j=0;j<m/2;j++){
                 mp[{g[n/2][j],g[n/2][m-j-1]}]++;
            }
            p10=mp[{1,0}]+mp[{0,1}];
            p11=mp[{1,1}];

            if(p11%2==0){
                ans+=p10;
            }
            else{
                if(p10>0){
                    ans+=p10;
                }
                else{
                    ans+=p10;
                    if(p11>0)
                    ans+=2;
                }
            }

        }
        else if(m%2==1){
            int p10=0;
            int p11=0;
           for(int i=0;i<n/2;i++){
                 mp[{g[i][m/2],g[n-i-1][m/2]}]++;
            }
            p10=mp[{1,0}]+mp[{0,1}];
            p11=mp[{1,1}];

            if(p11%2==0){
                ans+=p10;
            }
            else{
                if(p10>0){
                    ans+=p10;
                }
                else{
                    ans+=p10;
                    if(p11>0)
                    ans+=2;
                }
            }

        }


        return ans;
    }
};
