class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        const int n = a.size(), m = a[0].size();
        int ans = 0;
        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int ii = n - i - 1;
                int jj = m - j - 1;
                vector<int> b = {a[i][j], a[ii][j], a[i][jj], a[ii][jj]};
                int c0 = 0, c1 = 0;
                for (auto x : b) {
                    if (x == 0) c0++;
                    else c1++;
                }
                ans += min(c0, c1);
            }
        }
        int c00 = 0, c11 = 0, c01 = 0;
        if (n % 2 == 1) {
            for (int j = 0; j < m / 2; ++j) {
                int x = a[n/2][j], y = a[n/2][m-j-1];
                if (x + y == 0) c00++;
                else if (x + y == 1) c01++;
                else c11++;
            }
        }
        if (m % 2 == 1) {
            for (int i = 0; i < n / 2; ++i) {
                int x = a[i][m/2], y = a[n-i-1][m/2];
                if (x + y == 0) c00++;
                else if (x + y == 1) c01++;
                else c11++;
            }
        }
        if (n % 2 == 1 && m % 2 == 1) ans += a[n/2][m/2] == 1;
        ans += c01;
        if (c01 == 0 && c11 % 2 == 1) {
            ans += 2;
        }
        return ans;
    }
};
