class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int rows = grid.size();
        int cols = grid[0].size();
        int res = 0;

        for (int i=0, j=rows-1; i<j; ++i, --j) {
            auto& up = grid[i];
            auto& down = grid[j];

            for (int l=0, r=cols-1; l<r; ++l, --r) {
                int cnt = up[l] + up[r] + down[l] + down[r];
                res += min(cnt, 4-cnt);
            }
        }

        int rodd = 0;
        int rcnt = 0;
        if (rows % 2 == 1) {

            auto& row = grid[rows/2];
            for (int l=0, r=cols-1; l<r; ++l, --r) {
                int sum = row[l] + row[r];
                if (sum == 1)
                    rodd++;
                else rcnt += sum;
            }
        }

        int codd = 0;
        int ccnt = 0;
        if (cols % 2 == 1) {
            int k = cols/2;
            for (int i=0, j=rows-1; i<j; ++i, --j) {
                int sum = grid[i][k] + grid[j][k];
                if (sum == 1)
                    codd++;
                else ccnt += sum;
            }

            if (rows % 2 == 1)
                res += grid[rows/2][k];
        }

        res += rodd + codd;
        int rem = (rcnt + ccnt) % 4;
        if (rodd + codd == 0)
            res += rem;

        return res;
    }
};
