class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
          int n=grid.size();
          int m=grid[0].size();
         int ans =0;
         int ct1=0,ct2=0,ct=0;
         for(int j=0;j<(n+1)/2;j++){ 
            set<pair<int,int>> temp;
          for(int i=0;i<(m+1)/2;i++){
             // grid[j][i]   grid[j][m-1-i]   grid[n-1-j][i]  grid[n-1-j][m-1-i]
             temp.insert({j,i});
             temp.insert({j,m-1-i});
             temp.insert({n-1-j,i});
             temp.insert({n-1-j,m-1-i});
             if(temp.size()==1){
                if(grid[temp.begin()->first][temp.begin()->second]==1) ct++;
             }
             else if(temp.size()==2){
                  int p =0;
                  for(auto it : temp){
                    if(grid[it.first][it.second]) p++;
                  }
                 if(p==1) ct1++;
                 if(p==2) ct2++;

             }
             else{
                  int p =0;
                  for(auto it : temp){
                    if(grid[it.first][it.second]) p++;
                  }
                  if(p==1 || p==3){
                      ans++;
                  }
                  else if(p==2) ans+=2;

             }
             temp.clear();
          }
         }

         int rem = (ct+ct1*2+ct2*2)%4;
         cout << ct << ct1 << ct2 << endl;
         cout << rem << endl;
         if(rem==1){
            ans += 1;
            ans += ct1;
         }
         else if(rem==2){
            if(ct1!=0){
                ans += ct1;
            }
            else if(ct2!=0){
                ans += 2;
                ans+=ct1;
            }
         }
         else if(rem==3){
            ans += 1;
            if(ct1!=0){
                ans += ct1;
            }
            else if(ct2!=0){
                ans += 2;
                ans += ct1;
            }
         }
         else ans+=ct1;
      return ans;
    }
};
