class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size(), n=grid[0].size(), res=0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int ones = grid[i][j] + grid[i][n-1-j] + grid[m-1-i][j] + grid[m-1-i][n-1-j];
                if(ones==1 || ones==3){
                    res++;
                }
                else if(ones==2){
                    res+=2;
                }
            }
        }
        int rf=0,cf=0,ones=0;
        if(m%2){
            for(int i=0;i<n/2;i++){
                if(grid[m/2][i]!=grid[m/2][n-i-1]){
                    rf++;
                }
                else if(grid[m/2][i]){
                    ones+=2;
                }
            }
        }
        if(n%2){
            for(int i=0;i<m/2;i++){
                if(grid[i][n/2]!=grid[m-i-1][n/2]){
                    cf++;
                }
                else if(grid[i][n/2]){
                    ones+=2;
                }
            }
        }
        ones+=(rf*2+cf*2);
        ones%=4;
        res+=rf;
        res+=cf;
        if(ones==2 && rf+cf==0){
            res+=2;
        }
        if(m%2 && n%2){
            res+=grid[m/2][n/2];
        }
        return res;
    }
};
