class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        set<set<pair<int, int>>> grp;
        for (int i = 0; i < (n + 1) / 2; i++) {
            for (int j = 0; j < (m + 1) / 2; j++) {
                int x1 = i, y1 = j;
                int x2 = i, y2 = m - j - 1;
                int x3 = n - i - 1, y3 = j;
                int x4 = n - i - 1, y4 = m - j - 1;
                set<pair<int, int>> s;
                s.insert({x1, y1});
                s.insert({x2, y2});
                s.insert({x3, y3});
                s.insert({x4, y4});
                grp.insert(s);
            }
        }
        vector<int> extraone;
        int op = 0, total = 0;
        for(auto &g : grp){
            int one = 0, zero = 0;
            for(auto &[x , y] : g){
                if(grid[x][y]) one++;
                else zero++;
            }
            if(one && zero){
                if(one == zero) {
                    op += one;
                    extraone.push_back(one * 2);
                }
                else{
                    int mi = min(one, zero);
                    op += mi;
                    if(zero == mi){
                        total += (one + zero);
                    }
                }
            }
            else if(one) total += one;
        }
        // cout << total << " " << op << "\n";
        if(total & 1) op++, total--;
        if(total % 4 == 0) return op;
        int re = total % 4;
        sort(extraone.begin(), extraone.end());
        if(extraone.size() > 0 && re == 2 && extraone[0] == 2){
            return op;
        }
        return op + 2;
    }
};

