class Solution {
public:

    int figure(int a, int b, int c, int d) {
      int x = 0, y = 0 ;
      if(a == 1) x++ ; else y++ ;
      if(b == 1) x++ ; else y++ ;
      if(c == 1) x++ ; else y++ ;
      if(d == 1) x++ ; else y++ ;
      return min(4-x, 4-y) ;
    }

    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size() ;
        int m = grid[0].size() ;
        // cout<<n<<" "<<m<<endl ;
        int x = 0 ;
        for(int i=0;i<n/2;i++) {
          for(int j=0;j<m/2;j++) {
            x += figure(grid[i][j], grid[i][m-1-j], grid[n-i-1][j], grid[n-1-i][m-1-j]) ;
          }
        }
        int c = 0, d = 0 ;
        if(n%2 != 0) {
          int i = n/2 ;
          for(int j=0;j<m/2;j++) {
            if(grid[i][j] != grid[i][m-1-j])
            c++ ;
            else if(grid[i][j] == 1)
            d++ ;
          }
        }
        if(m%2 != 0) {
          int j = m/2 ;
          for(int i=0;i<n/2;i++) {
            // cout<<grid[i][j]<<" "<<grid[n-1-i][j]<<endl ;
            if(grid[i][j] != grid[n-1-i][j])
            c++ ;
            else if(grid[i][j] == 1)
            d++ ;
          }
        }
        // cout<<c<<" "<<d<<endl ;
        if((d*2)%4 != 0) {
          if(c == 0)
          c+=2 ;
        }
        x+=c ;
        if(n%2 != 0 && m%2 != 0) {
          if(grid[n/2][m/2] == 1)
          x++ ;
        }
        return x ;
    }
};
