class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int i, j, k, n = grid.size(), m = grid[0].size();
        vector<vector<int>> vis(n, vector<int>(m, 0));
        int ans = 0;
         int same=0;
          int diff=0;
        for (i = 0; i < n; i++) {
            for (j = 0; j < m; j++) {
                if (vis[i][j] == 1)
                    continue;
                vis[i][j] = 1;
                int o = (grid[i][j] == 1);
                int cnt = 1;
                int nj = m - j - 1, ni = n - i - 1;
                if (nj != j) {
                    cnt++;
                    if (grid[i][nj] == 1)
                        o++;
                    vis[i][nj] = 1;
                }
                if (ni != i) {
                    cnt++;
                    if (grid[ni][j] == 1)
                        o++;
                    vis[ni][j] = 1;
                }
                if (nj != j && ni != i) {
                    cnt++;
                    if (grid[ni][nj] == 1)
                        o++;
                    vis[ni][nj] = 1;
                }
                if (cnt == 4)
                    ans += min(o, 4 - o);
                else  if (cnt == 2){
                        if (o==2) same++;
                        else if (o==1) diff++;
                }
                else if (cnt==1 && o==1) ans++;
            }
        }
         same=same%2;
         if (same==1 && diff==0){
             ans+=2;
         }
          ans+=diff;
        return ans;
    }
};
