class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        ret = 0
        for i in range(m // 2):
            for j in range(n // 2):
                cnt = grid[i][j] + grid[i][n - 1 - j] + grid[m - 1 - i][j] + grid[m - 1 - i][n - 1 - j]
                cnt %= 4
                if cnt == 1 or cnt == 3:
                    ret += 1
                elif cnt == 2:
                    ret += 2
        cnt = 0
        t = 0
        if n % 2 == 1:
            for i in range(m // 2):
                if grid[i][n // 2] != grid[m - 1 - i][n // 2]:
                    t += 1
                elif grid[i][n // 2] == 1:
                    cnt += 2
        if m % 2 == 1:
            for j in range(n // 2):
                if grid[m // 2][j] != grid[m // 2][n - 1 - j]:
                    t += 1
                elif grid[m // 2][j] == 1:
                    cnt += 2
        if cnt % 4 != 0 and t == 0:
            t = 2
        ret += t
        if m % 2 == 1 and n % 2 == 1 and grid[m // 2][n // 2] == 1:
            ret += 1
        return ret
