class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        ans=0
        # 处理中轴线上
        def get(a):
            dif=0
            ones=0
            for i in range(len(a)//2):
                if a[i]==a[-(i+1)]:
                    if a[i]==1:
                        ones+=1
                else:
                    dif+=1
            return dif,ones

        def solve(a):
            # 需要保证两对1
            dif,ones=get(a)
            if ones&1:
                # pass
                if dif:
                    return dif
                else:
                    return 2
            return dif
        def handle(a,b):
            adif,aones=get(a)
            bdif,bones=get(b)
            dif,ones=adif+bdif,aones+bones
            if ones&1:
                if dif:
                    return dif
                else:
                    return 2
            else:
                return dif
        rows=len(grid)
        cols=len(grid[0])
        if rows&1 and cols&1:
            ans+=handle(grid[rows//2],[grid[i][cols//2]for i in range(rows)])
        elif rows&1:
            ans+=solve(grid[rows//2])
        elif cols&1:
            ans+=solve([grid[i][cols//2] for i in range(rows)])
        else:
            pass
        # 处理中心点上
        if len(grid)&1 and len(grid[0])&1 and grid[len(grid)//2][len(grid[0])//2]==1:
            ans+=1
        print(rows,cols,ans)
        for i in range(rows//2):
            for j in range(cols//2):
                a=[
                    grid[i][j],
                    grid[i][-(j+1)],
                    grid[-(i+1)][j],
                    grid[-(i+1)][-(j+1)],
                ]
                # print(i,j,a)
                c=[0,0]
                for k in a:
                    c[k]+=1
                ans+=min(c)
        return ans
