class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m = len(grid)
        n = len(grid[0])
        ans = 0
        for i in range(m // 2):
            for j in range(n // 2):
                s = grid[i][j] + grid[i][n-1-j] + grid[m-1-i][j] + grid[m-1-i][n-1-j]
                ans += min(s, 4-s)

        if m % 2 == 1:
            if n % 2 == 1:
                if grid[m//2][n//2] == 1:
                    ans += 1
                    grid[m//2][n//2] = 0

                did = False
                mid = grid[m//2]
                s = sum(mid)
                for i in range(n//2):
                    if mid[i] != mid[n-1-i]:
                        ans += 1
                        did = True

                mid = [row[n//2] for row in grid]
                s += sum(mid)
                for i in range(m // 2):
                    if mid[i] != mid[m-i-1]:
                        ans += 1
                        did = True

                if not did:
                    if s % 4 == 2:
                        ans += 2
            else:
                did = False
                mid = grid[m//2]
                s = sum(mid)
                for i in range(n//2):
                    if mid[i] != mid[n-1-i]:
                        ans += 1
                        did = True

                if not did:
                    if s % 4 == 2:
                        ans += 2

        else:
            if n % 2 == 1:
                did = False
                mid = [row[n//2] for row in grid]
                s = sum(mid)
                for i in range(m//2):
                    if mid[i] != mid[m-1-i]:
                        ans += 1
                        did = True

                if not did:
                    if s % 4 == 2:
                        ans += 2

        return ans
