class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size(),res=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                vector<int> v={grid[i][j],grid[i][m-j-1],grid[n-1-i][j],grid[n-i-1][m-1-j]};
                int z=0,o=0;
                for(int k=0;k<4;k++){
                    if(v[k]==1) o++;
                    else z++;
                }
                res+= min(z,o);
            }
        }
        int c=0,r=0,ch=0;
        if(n%2==1 && m%2==1 && grid[n/2][m/2]==1) res++;
        if(n%2){
            for(int i=0;i<m/2;i++){
                if(grid[n/2][i]!=grid[n/2][m-i-1]) ch++;
                else if(grid[n/2][i]==1) c++;

            }
        }
        if(m%2){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-1-i][m/2]) ch++;
                else if(grid[i][m/2]==1) c++;

            }
        }
        if(c%2==1 && ch==0){
            res+=2;
        }
        if(ch>0) res+=ch;
        return res;
    }
};
