class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
     int m=grid.size();
        int n=grid[0].size();
       int ans=0;
        //int extra=0;
        for(int i=0;i<m/2;i++)
        {

            for(int j=0;j<n/2;j++)
            {
               int z=0;
            z+=(grid[i][n-1-j])==0; 
            z+=(grid[i][j])==0; 
            z+=(grid[m-1-i][n-1-j])==0; 
            z+=(grid[m-1-i][j])==0; 
                 int o=4-z;
                if(z<=o)
                    ans+=z;
                else
                {
                    ans+=o;
                }

            //ans+=(maxi);
            }


        }

        int mis=0,extra=0;
        if (m%2==1)
    {   
             //int mis=0,extra=0;;
        for (int i=0;i<n/2;i++) 
        { 

            if (grid[m/2][i]!=grid[m/2][n-1-i])
            {
                mis++;
            }
            else 
            {
                if(grid[m/2][i]==1)
                    extra+=2;
            }


        } 

    } 

    if (n%2==1) 
    { 

        for (int i=0;i<m/2;i++) {   // 2 1

            if (grid[i][n/2]!=grid[m-1-i][n/2])
            {
               mis++;
            }
        else if(grid[i][n/2]==1)
                extra+=2;


    }
          // if(mis==0 && extra%4==2)
          //       ans+=2;
          //   else if(mis%2==0)
          //       ans+=mis;
          //   else 
          //   {
          //        ans+=mis;
          //   }
    }
         if(mis==0 && extra%4==2)
                ans+=2;
            else if(mis%2==0)
                ans+=mis;
            else 
            {
                 ans+=mis;
            }
        if(m&1 && n&1)
        ans+=(grid[m/2][n/2]==1);
        return ans;

    }
};

