class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        ans = 0

        for i in range(m // 2):
            for j in range(n // 2):
                tmp = grid[i][j] + grid[-i - 1][j] + grid[-i - 1][-j - 1] + grid[i][-j - 1]
                ans += min(tmp, 4 - tmp)

        if m % 2 and n % 2 and grid[m // 2][n // 2]:
            grid[m // 2][n // 2] = 0
            ans += 1

        tmp1 = tmp2 = 0
        a = b = 0
        if m % 2:
            for i in range(n // 2):
                tmp1 += 1 if grid[m // 2][i] != grid[m // 2][-i - 1] else 0
            a += sum(grid[m // 2])

        if n % 2:
            for i in range(m // 2):
                tmp2 += 1 if grid[i][n // 2] != grid[-i - 1][n // 2] else 0
                b += grid[i][n // 2] + grid[-i - 1][n // 2]
        ans += tmp1 + tmp2

        if (a + b - tmp1 - tmp2) % 4 == 2 and (tmp1 + tmp2) == 0:
            ans += 2
        return ans
