class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();        
        int m = grid[0].size();    
        int res = 0;

        for (int i = 0; i < n / 2; i++) {
            for (int j = 0; j < m / 2; j++) {
                int cnt0 = 0;
                int cnt1 = 0;

                if (grid[i][j] == 0) cnt0++; else cnt1++;
                if (grid[n - 1 - i][j] == 0) cnt0++; else cnt1++;
                if (grid[i][m - 1 - j] == 0) cnt0++; else cnt1++;
                if (grid[n - 1 - i][m - 1 - j] == 0) cnt0++; else cnt1++;

                res += 4 - max(cnt0, cnt1);
            }
        }

        int countz = 0;
        int counto = 0;

        if (n % 2 != 0) {
            int i = n / 2;
            for (int j  = 0; j < m / 2; j++) {
                if (grid[i][j] != grid[i][m - 1 - j]) {
                    res++;
                    countz++;
                } else {
                    if (grid[i][j] == 1) {
                        counto++;
                    }
                }
            }
        }
        if (m % 2 != 0) {
            int j = m / 2;
            for (int i = 0; i < n / 2; i++) {
                if (grid[i][j] != grid[n - 1 - i][j]) {
                    res++;
                    countz++;
                } else {
                    if (grid[i][j] == 1) {
                        counto++;
                    }
                }
            }
        }
        if (n % 2 != 0 && m % 2 != 0) {
            int center = grid[n / 2][m / 2];
            if (center == 1) res++;
        }

        if (counto % 2 != 0 && countz == 0) {
            res+=2;
        }

        return res;
    }
};
