class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int flips = 0;
        int ones = 0;
        for (int i = 0; i < grid.size() / 2; i++) {
            for (int j = 0; j < grid[0].size() / 2; j++) {
                int one = 0;
                one += grid[i][j];
                one += grid[grid.size() - 1 - i][j];
                one += grid[grid.size() - 1 - i][grid[0].size() - 1 - j];
                one += grid[i][grid[0].size() - 1 - j];
                // cout<<one<<endl;
                if (one == 4) {
                    ones += 4;
                } else if (one == 3) {
                    ones += 4;
                    flips += 1;
                } else if (one == 2) {
                    ones += 4;
                    flips += 2;
                } else {
                    flips += (one);
                }
            }
        }
        int p=0;
        int dif=0;
        int y = grid.size();
        int x = grid[0].size();
        if (x % 2 == 1 && y % 2 == 1) {
            for (int i = 0; i < grid.size() / 2; i++) {
                if (grid[i][grid[0].size() / 2] != grid[grid.size() - 1 - i][grid[0].size() / 2]) {
                    dif++;
                    // cout<<"a";
                }
                else if(grid[i][grid[0].size() / 2]==1){
                    p++;
                }
            }
            for (int i = 0; i < grid[0].size() / 2; i++) {
                if (grid[grid.size() / 2][i] !=grid[grid.size() / 2][grid[0].size() - 1 - i]) {
                    dif++;
                    // cout<<"b";
                }
                else if(grid[grid.size() / 2][i]==1){
                    p++;
                }
            }
            if (grid[grid.size() / 2][grid[0].size() / 2] == 1) {
                flips++;
                // cout<<"c";
            }
            flips+=dif;
            if(dif==0){
                flips+=(p%2)*2;
            }

        } else if (x % 2 == 1 && y % 2 == 0) {
            for (int i = 0; i < grid.size() / 2; i++) {
                if (grid[i][grid[0].size() / 2] != grid[grid.size() - 1 - i][grid[0].size() / 2]) {
                    dif++;
                    // cout<<"a";
                }
                else if(grid[i][grid[0].size() / 2]==1){
                    p++;
                }
            }
            flips+=dif;
            if(dif==0){
                flips+=(p%2)*2;
            }
        } else if (x % 2 == 0 && y % 2 == 1) {
            for (int i = 0; i < grid[0].size() / 2; i++) {
                if (grid[grid.size() / 2][i] !=grid[grid.size() / 2][grid[0].size() - 1 - i]) {
                    dif++;
                    // cout<<"b";
                }
                else if(grid[grid.size() / 2][i]==1){
                    p++;
                }
            }
            flips+=dif;
            if(dif==0){
                flips+=(p%2)*2;
            }
        }
        return flips;
    }
};
