class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size(), ans = 0;
        vector <vector <int>> cnt(n, vector <int> (m));
        int ones = 0;
        int choosezerocost = 1e6, chooseonecost = 1e6;
        for (int i = 0; i < n; i ++) {
            for (int j = 0; j < m; j ++) {
                if (i == n - 1 - i && j == m - 1 - j) {
                    if (grid[i][j]) ans ++;
                }
                else if (i == n - 1 - i) {
                    if (j < m - 1 - j) {
                        cnt[i][j] = grid[i][j] + grid[i][m - 1 - j];
                        if (cnt[i][j] <= 2 - cnt[i][j]) {
                            chooseonecost = min(chooseonecost, 2 * (1 - cnt[i][j]));
                        }
                        else {
                            ones ++;
                            choosezerocost = min(choosezerocost, 2 * (cnt[i][j] - 1));
                        }
                        ans += min(cnt[i][j], 2 - cnt[i][j]);
                    }
                }
                else if (j == m - 1 - j) {
                    if (i < n - 1 - i) {
                        cnt[i][j] = grid[i][j] + grid[n - 1 - i][j];
                        if (cnt[i][j] <= 2 - cnt[i][j]) {
                            chooseonecost = min(chooseonecost, 2 * (1 - cnt[i][j]));
                        }
                        else {
                            ones ++;
                            choosezerocost = min(choosezerocost, 2 * (cnt[i][j] - 1));
                        }
                        ans += min(cnt[i][j], 2 - cnt[i][j]);
                    }
                }
                else {
                    if (i < n - 1 - i && j < m - 1 - j) {
                        cnt[i][j] = grid[i][j] + grid[i][m - 1 - j] + grid[n - 1 - i][j] + grid[n - 1 - i][m - 1 - j];
                        ans += min(cnt[i][j], 4 - cnt[i][j]);
                    }
                }
            }
        }
        if (ones & 1) {
            ans += min(choosezerocost, chooseonecost);
        }
        return ans;
    }
};
