class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;
        for(int i = 0; i < n/2; i++) {
            for(int j = 0; j < m/2; j++) {
                vector<int>cnt(2, 0);
                cnt[grid[i][j]]++;
                cnt[grid[n-i-1][j]]++;
                cnt[grid[i][m-j-1]]++;
                cnt[grid[n-i-1][m-j-1]]++;
                ans += min(cnt[0], cnt[1]);
            }
        }
        int plus = 0, one = 0;
        if(n&1) {
           for(int i = 0; i < m/2; i++) {
               if(grid[n/2][i] != grid[n/2][m-i-1]) {
                   plus++;
               }
               else if(grid[n/2][i] == 1) {
                   one += 2;
               }
           } 
        }
        if(m&1) {
           for(int i = 0; i < n/2; i++) {
               if(grid[i][m/2] != grid[n-i-1][m/2]) {
                   plus++;
               }
               else if(grid[i][m/2] == 1) {
                   one += 2;
               }
           } 
        }
        if((n&1) && (m&1) && (grid[n/2][m/2])) {
            ans++;
        }
        ans += plus;
        if((one%4 == 2) && (plus == 0)) {
            ans += 2;
        }
        // switch(one%4) {
        //     case 0:
        //         ans += plus;
        //         break;
        //     case 2:
        //         if(plus > 0) {
        //             ans += plus;
        //         }
        //         else {
        //             ans += 2;
        //         }
        //         break;                
        // };
        return ans;
    }
};
