class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int ans=0;
        if(n==1 && m==2 || n==2 && m==1 || n==1 && m==3 || n==3 && m==1 || n==1 && m==1) {
            int sum=0;
            for(int i=0;i<n;i++){
                for(int j=0;j<m;j++){
                    sum+=grid[i][j];
                }
            }
            return sum;
        } 
        for(int i=0;i<n/2;i++){
            int altrow=n-1-i;
            int l=0,r=m-1;
            while(l<r){
                int sum=grid[i][l]+grid[i][r]+grid[altrow][l]+grid[altrow][r];
                ans+=min(sum,4-sum); l++; r--;
            }
        }
        // return ans;
                    int cnt=0,cnt1=0;

        if(n%2) {
            int l=0,r=m-1;
            while(l<r){
                if(grid[n/2][l]!=grid[n/2][r]) cnt1++; 
                else if(grid[n/2][l]==grid[n/2][r] && grid[n/2][l]==1) cnt++;
                l++; r--;
            }
            if(m%2) if(grid[n/2][m/2]) ans++;
        }
        if(m%2){
            int l=0,r=n-1;
            while(l<r){
                if(grid[l][m/2]!=grid[r][m/2]) cnt1++;
                else if(grid[l][m/2]==grid[r][m/2] && grid[l][m/2]==1) cnt++;
                l++; r--;
            }
        }
        ans+=cnt1;
        if(cnt%2 && cnt1==0) ans+=2;
        return ans;
    }
};
