class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int cntz = 0, cnto = 0;
                int x = n-1-i, y = m-1-j;
                cntz = (grid[i][j] == 0) + (grid[i][y] == 0) + (grid[x][j] == 0) + (grid[x][y] == 0);
                cnto = (grid[i][j] == 1) + (grid[i][y] == 1) + (grid[x][j] == 1) + (grid[x][y] == 1);
                ans += min(4 - cnto, 4 - cntz);
            }
        }
        int cnts = 0, cntd = 0;
        if(n & 1){
            for(int i=0;i<m/2;i++){
                if(grid[n/2][i] == grid[n/2][m-1-i])
                    cnts += grid[n/2][i];
                else 
                    cntd += 1;
            }
        }
        if(m & 1){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2] == grid[n-i-1][m/2])
                    cnts += grid[i][m/2];
                else 
                    cntd += 1;
            }
        }
        if(cnts % 2 == 1){
            if(cntd > 0) ans += cntd;
            else ans += 2;
        }
        else ans += cntd;
        if((n % 2 == 1) && (m % 2 == 1) && grid[n/2][m/2] == 1) ans++;
        return ans;
    }
};
