class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0;
        for(int i = 0 , j = grid.size()-1; i < j ; i++,j--)
        {
            for(int x = 0 , y = grid[0].size()-1; x < y ;x++,y--)
            {
                int c = grid[i][x] + grid[i][y] + grid[j][x] + grid[j][y];
                ans += min(4-(c),c);
            }
        }
        bool flag = true;
         int t = 0;
            int f = 0;
        if(grid.size()&1)
        {
            int i = grid.size()/2;

            for(int x = 0 , y = grid[0].size() -1  ; x < y ;x++,y--)
            {
                if(grid[i][x]!= grid[i][y])
                f++;
                else if(grid[i][x]==1 && grid[i][y]==1)
                t++;
            }

        }
        else flag = false;

         if(grid[0].size()&1)
        {
            int i = grid[0].size()/2;

            for(int x = 0 , y = grid.size() -1  ; x < y ;x++,y--)
            {
                if(grid[x][i]!= grid[y][i])
                f++;
                else if(grid[x][i]==1 && grid[y][i]==1)
                t++;
            }

        }
        else flag = false;
        if(flag)
        {
            if(grid[grid.size()/2][grid[0].size()/2]==1)
            ans++;
        }
        if((t&1) && (f==0))
        {
            ans+=2;
        }
        ans += f;
        return ans;
    }
};
