class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        rows, cols = len(grid), len(grid[0])
        ans = 0

        for i in range(rows // 2):
            for j in range(cols // 2):
                arr = [0, 0]
                arr[ grid[i][j]] += 1
                arr[grid[i][cols - 1 - j]] += 1
                arr[grid[rows - 1 - i][j]] += 1
                arr[grid[rows - 1 - i][cols - 1 - j]] += 1
                ans += min(arr)
        extras = 0
        to_flip = 0
        # print(ans)
        if rows % 2 == 1:
            for j in range((cols + 1) // 2):
                if grid[rows // 2][j] == grid[rows // 2][cols - 1 - j] == 1:
                    extras += 2
                if grid[rows // 2][j] != grid[rows // 2][cols - 1 - j]:
                    to_flip += 1
        # print(extras, ans)

        if cols % 2 == 1:
            for j in range((rows + 1) // 2):
                if grid[j][cols // 2] == grid[rows - 1 - j][cols // 2] == 1:
                    extras += 2
                if grid[j][cols // 2] != grid[rows - 1 - j][cols // 2]:
                    to_flip += 1
        # print(ans, to_flip, extras)

        if rows % 2 == cols % 2 == 1 and grid[rows // 2][cols // 2] == 1:
            ans += 1

        if extras % 4 == 2:
            if to_flip > 0:
                return ans + to_flip
            ans += 2
        return ans + to_flip
