class Solution {
    public int minFlips(int[][] grid) {
        int t = 0;
        for (int i = 0; i < Math.floorDiv(grid.length,2); i++) {
            for (int j = 0; j < Math.floorDiv(grid[0].length,2); j++) {
                t += 2-Math.abs(grid[i][j]+grid[grid.length-1-i][j]+grid[i][grid[0].length-1-j]+grid[grid.length-1-i][grid[0].length-1-j]-2);
            }
        }
        int t2 = 0;
        int t3 = 0;
        if (grid.length%2 == 1) {
            for (int i = 0; i < Math.floorDiv(grid[0].length,2); i++) {
                t3 += grid[Math.floorDiv(grid.length,2)][i]^grid[Math.floorDiv(grid.length,2)][grid[0].length-1-i];
                t2 += grid[Math.floorDiv(grid.length,2)][i]+grid[Math.floorDiv(grid.length,2)][grid[0].length-1-i];
            }
        }
        if (grid[0].length%2 == 1) {
            for (int i = 0; i < Math.floorDiv(grid.length,2); i++) {
                t3 += grid[i][Math.floorDiv(grid[0].length,2)]^grid[grid.length-1-i][Math.floorDiv(grid[0].length,2)];
                t2 += grid[i][Math.floorDiv(grid[0].length,2)]+grid[grid.length-1-i][Math.floorDiv(grid[0].length,2)];
            }
        }
        if (t3 == 0 && t2%4==2) {
            t+=2;
        }
        t += t3;
        if ((grid.length*grid[0].length)%2==1) {
            t += grid[Math.floorDiv(grid.length,2)][Math.floorDiv(grid[0].length,2)];
        }
        return t;
    }
}
